DROP PROCEDURE IF EXISTS sp_register_student_to_course; 
Delimiter $$
create procedure sp_register_student_to_course (IN in_course_id bigint, IN in_banner_id varchar(10))
begin
		DECLARE local_user_id bigint DEFAULT 0;
        DECLARE local_role_id bigint DEFAULT 0;
		SELECT u.id INTO local_user_id FROM user as u WHERE u.banner_id = in_banner_id;
        SELECT r.id INTO local_role_id FROM role as r WHERE r.role = "STUDENT";
        INSERT INTO coursetouser(user_id, course_id, role_id) VALUE(local_user_id, in_course_id, local_role_id);
end$$
DELIMITER ;
